import React, { Component } from 'react';
//import logo from './logo.svg';
import icon from './icon2.png';
import Button from '@material-ui/core/Button';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.onmovemouse = this.onmovemouse.bind(this);
  }
  onmovemouse(event) {
    var balls = document.getElementsByClassName("ball");
    var x = event.clientX * 100 / window.innerWidth + "%";
    var y = event.clientY * 100 / window.inner + "%";
    //event.clinetX => get the horizontal coordinate of the mouth
    for(var i=0;i<2;i++){
      balls[i].style.left = x;
      balls[i].style.top = y;
      // balls[i].style.transform = "translate(-"+x+"")"
    }

  }
  render() {
  return (
    <div className="App">
      <div className="eyes">
        <div className="eye">
          <div className="ball" onmovemouse={this.onmovemouse}>
          </div>
        </div>
      </div>
      {/* <script>
        var balls = document.getElementsByClassName("ball");
        document.onmousemove = function() {
          var x = event.clientX * 100 / window.innerWidth + "%";
          var y = event.clientY * 100 / window.inner + "%";
        }
      </script> */}
      <img src={icon} style={{height: '10em', marginTop: '-60px', zIndex: '2'}}></img>
      <a href='https://www.linkedin.com/in/julia-opatrny/' target="_blank" style={{textDecoration: 'none', marginTop: '-10px'}}><Button className="Button" disableElevation

      // style={{backgroundColor: '#ff0d4d', 
      // margin: '10px', 
      // color: 'white',
      // borderRadius: 0,
      // fontSize: "18px",
      // width: '200px'
      // }}
      >About Me</Button></a>
      <a href='https://github.com/jsopatrny' target="_blank" style={{textDecoration: 'none'}}><Button disableElevation className="Button"
      >Projects</Button></a>
      <a href='https://www.instagram.com/tiny_slimy/' target="_blank" style={{textDecoration: 'none'}}><Button disableElevation className="Button"
      >Resume</Button></a>
      {/* <p style={{fontFamily: 'Open Sans'}}>Contact:</p>
      <p>jsopatrny@gmail.com 	âœ¿  917-208-2775</p> */}
      {/* <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
